import './App.css';

const Task =() => {

  const [ Tasks , setTask ]=useState({ 
      id :'1' ,
      text : 'studing js',
      reminder : 'true',
  },
  {
      id :'2' ,
      text : 'studing js',
      reminder : 'false',
  },
  {
      id :'3' ,
      text : 'studing js',
      reminder : 'true',
  })
  const taskStayle ={
    color:'red',
    curosir:'pointer'
  }
return (
  <div>
    {Tasks.map((task) =>(<h3 style={taskStayle} key={task.id}>{task.text}</h3>))
    } 

       </div>

)

}
export default Task
